import React from 'react'

function body() {

  return (
    <div className="about">
    <div className="paragraph">
    <h1 style={{"border" : "2px solid #e90349", "width" : "fit-content", "padding" : "10px"}}>
    WHY ME?
    </h1>
      <h1 style={{"background" : "#e90349", "width" : "fit-content", "color": "white", "padding" : "10px"}}>
        Diverse Skillset
        </h1>
    <p>
    Hi, I'm Adnan. I am a versatile graphic designer / creative technologist, who covers multiple digital platforms, from UX and UI, video production, web skins, HTML5 banners and applications to digital static graphics, OOH and DOOH. My position includes working with creative agencies to build and localise worldwide campaigns. Clients I have worked with are Oracle, EA, Sage, F5, Facebook, NetJets, Supercell, IHG, Indeed, Fitbit, Twitch, Twitter and many more.
    </p>
    </div>
    </div>
  );
}

export default body;
