import '../../_css/Work.css'
import React from 'react'
import { useEffect, useState } from 'react';
import gsap from 'gsap'
import ScrollToPlugin from 'gsap/ScrollToPlugin';
import ScrollTrigger from 'gsap/ScrollTrigger'
import { LazyLoadImage } from 'react-lazy-load-image-component';
import 'react-lazy-load-image-component/src/effects/blur.css';

gsap.registerPlugin(ScrollToPlugin, ScrollTrigger)

function Work(data) {

  useEffect(() => {

    const isElementOutViewport = (el) => {
      var rect = el.getBoundingClientRect();
      console.log(rect);
      return rect.bottom < 0 || rect.right < 0 || rect.left > window.innerWidth || rect.top > window.innerHeight;
    }
    const advent = document.querySelectorAll(".advent")

  window.addEventListener("scroll", () => {
   if(isElementOutViewport(advent)){

   }else{
     advent.classList.add("opacityChange")
   }
  })
  }, [])


  return (
    <>

  <h1 className="projectTitle">{data.title}</h1>

  <section className="panel">

  <LazyLoadImage effect="blur" src={data.image1} alt={"image"}/>
  <LazyLoadImage effect="blur" src={data.image2} alt={"image"}/>
  <LazyLoadImage effect="blur" src={data.image3} alt={"image"}/>
  <LazyLoadImage effect="blur" src={data.image4} alt={"image"}/>
  <img className="advent" src={data.image5} alt={"image"}/>
  <LazyLoadImage effect="blur" src={data.image6} alt={"image"}/>

  </section>
   </> 
  )
}

export default Work;
