import './App.css';
import react, {useState} from 'react'
import logo from './img/logo.png'
import bg from './img/bg.jpg'
import p1 from './img/p1.png'

function App() {
const [image, setImage] = useState([])    
const [number, setNum] = useState(0)


const gen = () => {
  const finder= document.querySelector(".Finder")
  finder.style.opacity = "0.2"
  finder.classList.add("Disabled")
    const checker = setInterval(() => {
  document.querySelector(".Finder").style.opacity = "1"
  finder.classList.remove("Disabled")
      clearInterval(checker)
    },5000)

    const list = (
    <li key={number} className='slideDown'>
    <img className="genImage" src={'https://source.unsplash.com/random/200x200/?portrait='+number} />
  </li>)

    setNum(number+1)
    setImage([list, ...image])
  }

  return (
    <div className="App">
      <div className="Finder" onClick={() => gen(number)}>
        Generate Photo
      </div> <img className="p1" width={"70"} src={p1} />
      <div className="DataBox">
        <p>Selfies Made Simple</p> 
        <ul className="data">
        {image}
        </ul>
      </div>
      <img className="bg" width={"50%"} src={bg} />

      <img width="100" src={logo}/>
      <p className='footer'>&copy; Adiali by Adnan Ali</p>
    </div>
  );
}

export default App;
